<?class RSS{	public function RSS()	{		require_once ('mysql_connect.php');	}		public function GetFeed()	{		return $this->getDetails() . $this->getItems();	}		private function dbConnect()	{		dbconnect();	}		private function getDetails()	{		$detailsTable = "wp_mcr_batches";		$this->dbConnect();		$query = "SELECT * FROM ". $detailsTable;		$result = mysql_query ($query);				while($row = mysql_fetch_array($result))		{			$details = '<?xml version="1.0" encoding="ISO-8859-1" ?>					<rss version="2.0" xmlns:atom="http://www.w3.org/2005/Atom">						<channel>							<atom:link href="http://localhost:8888/digitalstylistnetwork/wp-content/plugins/mcr/upload/rss.php" rel="self" type="application/rss+xml" />							<title>MCR</title>							<link>http://google.com</link>							<description>MCR</description>							';		}		return $details;	}		private function getItems()	{		$itemsTable = "wp_mcr_batches";		$this->dbConnect();		$query = "SELECT * FROM ". $itemsTable;		$result = mysql_query ($query);		$items = '';		while($row = mysql_fetch_array($result))		{			$items .= '<item>		<title>'.$row["post_id"].'</title>      <description>&lt;img src="http://25.media.tumblr.com/8779406a06cbf2313623eacc621197e1/tumblr_mnin9ukQzB1sswf4jo1_400.png"/&gt;&lt;br/&gt;&lt;br/&gt;&lt;p&gt;&lt;a href="http://localhost:8888/digitalstylistnetwork/?page_id=7&amp;i=3" target="_blank"&gt;Vote for this at digitalstylistnetwork&lt;/a&gt;&lt;/p&gt;</description>      <link>'.$row["pic_url"].'</link>      <pubDate>Tue, 28 May 2013 12:03:30 -0400</pubDate>      </item>';		}		$items .= '</channel>				 </rss>';		return $items;	}}?>